mapper.select:
SELECT * FROM (SELECT ROWNUM AS RNUM, N.* FROM (SELECT DONATION_NO AS donationNo, DONATION_TITLE AS donationTitle, DONATION_CONTENT AS donationContent, DONATION_TYPE AS donationType, DONATION_FILENAME AS donationFilename, DONATION_FILEPATH AS donationFilepath, DONATION_FILEPATH_ADD AS donationFilepathAdd, DONATION_GOAL_MONEY AS donationGoalMoney, DONATION_NOW_MONEY AS donationNowMoney, DONATION_END_DATE AS donationEndDate, DONATION_START_DATE AS donationStartDate, DONATION_WRITER AS donationWriter, DONATION_STATE AS donationState, DONATION_NEGATIVE AS donationNegative FROM DONATION
where donation_type like '%'||#{type}||'%'
ORDER BY DONATION_NO DESC) N ) WHERE RNUM BETWEEN #{start} AND #{end}
