select.if:
SELECT * FROM (SELECT ROWNUM AS RNUM, N.* FROM (SELECT TALENT_NO AS talentNo, TALENT_TITLE AS talentTitle, TALENT_WRITER AS talentWriter, TALENT_CONTENT AS talentContent, TALENT_COUNT AS talentCount, TALENT_PART AS talentPart, TALENT_FILENAME AS talentFilename, TALENT_FILENPATH AS talentFilepath, TALENT_ENROLL_DATE AS talentEnrollDate FROM TALENT
where TALENT_PART = #{type}
ORDER BY TALENT_NO DESC) N ) WHERE RNUM BETWEEN #{start} AND #{end}
